mapper.select:
select t.* ,sku.title as "sku.title",sku.img as "sku.img",sku.original_price as "sku.originalPrice", sku.price as "sku.price",sku.stock as "sku.stock",sku.freeze_stock as "sku.freezeStock", s.id as "supplier.id",s.shop_name as "supplier.shopName",s.shop_icon as "supplier.shopIcon", s.address as "supplier.address",spu.id as "spu.id",spu.status as "spu.status" from t_unimall_cart t,t_unimall_sku sku,t_unimall_spu sku,t_unimall_supplier s
t.sku_id = sku.id and sku.spu_id = spu.id and sup.supplier_id = s.id
and t.user_id=#{userId}
and t.id in
#{item}

select * from t_unimall_cart t


    and t.id = #{id}


    and t.sku_id = #{sku_id}


    and t.user_id = #{user_id}


    and t.num = #{num}


    and t.gmt_update = #{gmt_update}
